import React, { Component } from "react";
import { connect } from "react-redux";
import { Field, reduxForm, reset } from "redux-form";
import { saveComment } from "../Actions/PostActions";
import { required } from "../Helpers/ReduxFormValidation";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import _ from "lodash";
import "../Layout/Navbar.css";
import TextField from "@material-ui/core/TextField";

const styles = theme => ({
  card: {
    width: 490,
    height: 320,
    marginLeft: 600,
    marginTop: "-45%"
  },

  tit: {
    textDecoration: "none"
  },
  title: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif"
  },
  textField: {
    marginLeft: 30,
    width: 240,
    padding: 5
  },
  pos: {
    marginBottom: 12
  },
  reg: {
    marginLeft: 35
  },
  login: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif",
    marginLeft: 30,
    color: " #003300"
  },
  cssLabel: {
    "&$cssFocused": {
      color: " #003300",
      fontSize: 20
    }
  },
  cssFocused: {},
  cssUnderline: {
    "&:after": {
      borderBottomColor: " #003300"
    }
  },
  cssOutlinedInput: {
    "&$cssFocused $notchedOutline": {
      borderColor: " #003300"
    }
  },
  notchedOutline: {},
  bootstrapRoot: {
    "label + &": {
      marginTop: theme.spacing.unit * 3
    }
  },
  bootstrapInput: {
    borderRadius: 6,
    backgroundColor: " #003300",
    border: "1px solid  #003300",
    fontSize: 16,
    width: "600px",
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 6,
      borderColor: " #003300",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  },
  bootstrapFormLabel: {
    fontSize: 20
  },
  dense: {
    marginTop: 25
  },
  input: {
    marginLeft: 5,
    fontSize: 20
  },
  paper: {
    position: "absolute",
    width: theme.spacing.unit * 50,
    backgroundColor: "#757e75e1 !important",
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: "none"
  },
  register: {
    fontSize: 15,
    fontFamily: "'Montserrat', sans-serif",
    color: " #003300"
  }
});

class AddComment extends Component {
  onSubmit(values) {
    this.props.saveComment(values, this.props.id, this.props.uid).then(() => {
      this.props.dispatch(reset("CreateCommentForm"));
    });
  }

  renderComment(field) {
    const {
      meta: { touched, error }
    } = field;
    const errStyle = {
      borderColor: "red"
    };
    return (
      <div>
        <textarea
          className="form-control"
          style={touched && error ? errStyle : null}
          {...field.input}
        />
      </div>
    );
  }

  render() {
    const { handleSubmit, classes } = this.props;
    return (
      <form onSubmit={handleSubmit(this.onSubmit.bind(this))}>
        <TextField
          className={classes.textField}
          autoComplete="current-password"
          variant="outlined"
          InputLabelProps={{
            classes: {
              root: classes.cssLabel,
              focused: classes.cssFocused
            }
          }}
          InputProps={{
            classes: {
              root: classes.cssOutlinedInput,
              focused: classes.cssFocused,
              notchedOutline: classes.notchedOutline,
              input: classes.input
            }
          }}
          className="form-control"
        />
        <Field
          name="content"
          label="UpiÅ¡i svoj komentar..."
          component={this.renderComment}
          validate={required}
        />
        <Field
          name="name"
          label="Ime/username "
          component={this.renderComment}
          validate={required}
        />
        <div className="d-flex justify-content-end mt-1">
          <button type="submit" className="btn btn-success btn-sm">
            Save
          </button>
        </div>
      </form>
    );
  }
}
AddComment.propTypes = {
  classes: PropTypes.object.isRequired
};
export default reduxForm({
  form: "CreateCommentForm"
})(
  connect(
    (state, ownProps) => ({ uid: state.user.uid }),
    { saveComment }
  )(withStyles(styles))(AddComment)
);
